  `user_id` int(11) DEFAULT NULL COMMENT '外键关联用户表主键，表明文章是谁发布的',
  PRIMARY KEY (`a_id`),
  KEY `t_article_t_user_u_id_fk` (`user_id`),
  CONSTRAINT `t_article_t_user_u_id_fk` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`u_id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8 COMMENT='游戏测评文章表';
