DROP TABLE IF EXISTS `t_favorite`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_favorite` (
  `user_id` int(11) NOT NULL COMMENT '外键关联用户表主键，表示是哪个用户收藏',
  `game_id` int(11) NOT NULL COMMENT '外键关联游戏资讯表主键，表示收藏的游戏信息',
  `gmt_create` datetime DEFAULT NULL COMMENT '创建时间，表示用户收藏这个游戏的时间',
  `gmt_modified` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`user_id`,`game_id`),
  KEY `t_favorite_t_game_g_id_fk` (`game_id`),
  CONSTRAINT `t_favorite_t_game_g_id_fk` FOREIGN KEY (`game_id`) REFERENCES `t_game` (`g_id`),
  CONSTRAINT `t_favorite_t_user_u_id_fk` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`u_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='游戏收藏表';
/*!40101 SET character_set_client = @saved_cs_client */;

