DROP TABLE IF EXISTS `t_game`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_game` (
  `g_id` int(11) NOT NULL AUTO_INCREMENT,
  `g_name` varchar(255) DEFAULT NULL COMMENT '游戏名称',
  `cover` varchar(255) DEFAULT NULL COMMENT '游戏封面链接',
  `developer` varchar(255) DEFAULT NULL COMMENT '游戏开发商',
  `publisher` varchar(255) DEFAULT NULL COMMENT '游戏发行商',
  `game_score` int(2) DEFAULT NULL COMMENT '游戏评级星数，1-5颗星',
  `is_deleted` int(1) DEFAULT NULL COMMENT '逻辑删除，1：删除，0：保留',
  `release_time` varchar(20) DEFAULT NULL COMMENT '游戏发行时间',
  `gmt_create` datetime DEFAULT NULL COMMENT '创建时间',
  `gmt_modified` datetime DEFAULT NULL COMMENT '修改时间',
  `genres_id` int(11) DEFAULT NULL COMMENT '外键关联游戏类型表主键',
  `game_review` text COMMENT '游戏简介',
  PRIMARY KEY (`g_id`),
  KEY `fk_game_genres` (`genres_id`),
  CONSTRAINT `fk_game_genres` FOREIGN KEY (`genres_id`) REFERENCES `t_genres` (`gg_id`)
) ENGINE=InnoDB AUTO_INCREMENT=60 DEFAULT CHARSET=utf8 COMMENT='游戏表，用于存储游戏信息';
/*!40101 SET character_set_client = @saved_cs_client */;

