DROP TABLE IF EXISTS `t_game_comment`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_game_comment` (
  `game_comment_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `game_id` int(11) NOT NULL COMMENT '外键关联游戏表主键',
  `user_id` int(11) NOT NULL COMMENT '外键关联用户表主键',
  `comment_content` varchar(255) DEFAULT NULL COMMENT '游戏评论内容',
  `is_deleted` tinyint(1) DEFAULT NULL COMMENT '逻辑删除，1:删除，0:保留',
  `gmt_create` datetime DEFAULT NULL COMMENT '创建时间（游戏评论发表时间）',
  `gmt_modified` datetime DEFAULT NULL COMMENT '修改时间(评论修改时间)',
  PRIMARY KEY (`game_comment_id`),
  KEY `t_game_comment_t_game_g_id_fk` (`game_id`),
  KEY `t_game_comment_t_user_u_id_fk` (`user_id`),
  CONSTRAINT `t_game_comment_t_game_g_id_fk` FOREIGN KEY (`game_id`) REFERENCES `t_game` (`g_id`),
  CONSTRAINT `t_game_comment_t_user_u_id_fk` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`u_id`)
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=utf8 COMMENT='游戏评论表';
/*!40101 SET character_set_client = @saved_cs_client */;

