INSERT INTO `t_game_comment` (`game_comment_id`, `game_id`, `user_id`, `comment_content`, `is_deleted`, `gmt_create`, `gmt_modified`) VALUES (1,44,1,'这是一个好游戏',0,'2022-03-23 14:53:57','2022-03-24 14:42:07'),(2,44,1,'好游戏，尽在知行成游戏网。。。',0,'2022-03-23 14:53:56','2022-03-24 14:42:34'),(3,44,2,'知行成游戏网是一个好网站',0,'2022-03-23 14:53:59','2022-03-24 14:42:07'),(8,44,21,'知行成游戏',0,'2022-03-23 20:22:08','2022-03-24 14:42:07'),(9,44,21,'知行成游戏，《银河战士：萨姆斯归来》是由银河战士系列创始人坂本贺勇担任监督',0,'2022-03-23 20:22:42','2022-03-24 14:42:07'),(10,44,21,'重制版。 [1] 《银河战士：萨姆斯归来》将在201',0,'2022-03-23 20:23:16','2022-03-24 12:39:54'),(11,44,21,'7年9月15日发售。 [2] 会推出普通版外，任天堂还准',0,'2022-03-23 20:23:22','2022-03-24 12:39:54'),(12,44,21,'好游戏',0,'2022-03-23 20:24:55','2022-03-24 12:39:54'),(13,44,21,'值得一玩',0,'2022-03-23 20:25:20','2022-03-24 12:39:54'),(14,44,21,'由银河战士系列创始人坂本贺勇担任监督，水银蒸汽制作的系列新作',0,'2022-03-23 20:26:43','2022-03-24 12:39:54'),(15,44,21,'版游戏包括特殊游戏包装盒和原声CD，该原声CD包含系列25首配乐。将在2017年9月15日发售。 [2] 会推出普通版外，任天堂还准备了限量版游戏。限量版游戏包括特殊游戏包装盒和原声CD，该原声CD包含系列25首配乐。任天堂还在官方商城里单独上架只有周边没有游戏本体的特别收藏版。将在2017年9月15日发售。 [2] 会推出普通版外，任天堂还准备了限量版游戏。限量版游戏包括特殊游戏包装盒和原声CD',0,'2022-03-23 20:28:51','2022-03-24 12:39:57'),(16,44,21,'由银河战士系列创始人坂本贺勇担任监督，水银蒸汽制作的系列新作，是GB平台上的《银河战士2：萨姆斯回归（Metroid II: Return of Samus）》的重制版。 [1] 《银河战士：萨姆斯归来》将在2017年9月15',0,'2022-03-23 20:30:38','2022-03-23 20:30:38'),(17,43,21,'GTA系列游戏一直都是挺不错的。',0,'2022-03-23 20:40:14','2022-03-23 20:40:14'),(18,25,2,'银河战士-零点人物是一款非常不错的动作类游戏。',0,'2022-03-23 22:22:58','2022-03-23 22:22:58'),(19,44,1,'虽然寡人为吴国主公，但是这款游戏依然深深地吸引了我。',0,'2022-03-24 16:42:11','2022-03-24 16:42:11'),(20,44,2,'吾以汉室之名，借这款游戏建立蜀国，与魏国、吴国三足鼎立。',0,'2022-03-24 16:44:08','2022-03-24 16:44:08'),(21,44,3,'蜀国和吴国联姻，我不幸的成为了政治工具，借此游戏消愁。',0,'2022-03-24 16:45:21','2022-03-24 16:45:21'),(22,44,11,'长坂坡救完少主阿斗，闲暇之余，玩玩游戏。',0,'2022-03-24 16:46:47','2022-03-24 16:46:47'),(23,24,9,'这款游戏的背景音乐有点恐怖....',0,'2022-03-26 19:35:44','2022-03-26 19:35:44'),(24,24,1,'好游戏尽在知行成游戏网',0,'2022-04-05 22:52:35','2022-04-05 22:52:35');
